mapper.select:
SELECT seq, memId, ref, step, depth, title, content, created, del, count FROM (SELECT row_number() OVER(ORDER BY ref DESC, step ASC) AS rnum, seq, memId, ref, step, depth, title, content, created, del, count FROM tipboard
WHERE title LIKE concat('%', #{search}, '%')
WHERE content LIKE concat('%', #{search}, '%')
WHERE memId=#{search}
ORDER BY ref DESC, step ASC) a WHERE rnum BETWEEN ${pageStart} AND ${pageEnd}

mapper.insert:
INSERT INTO hitcount (bbsSeq, memId, latest) VALUES (#{bbsSeq}, #{readerId}, sysdate)
ON DUPLICATE KEY
UPDATE latest=sysdate;

mapper.update:
UPDATE tipboard
SET count = count + 1
WHERE seq = #{seq}

UPDATE tipboard
SET content=#{content}, title=#{title}
WHERE seq=#{seq}

UPDATE tipboard
SET del = 1
WHERE seq=#{seq}
