mapper.select:
SELECT boardno, memId, ref, step, depth, title, content, category, hitcount, del, created FROM (SELECT row_number() OVER(ORDER BY ref DESC, step ASC) AS rnum, boardno, memId, ref, step, depth, title, content, category, hitcount, del, created FROM tipboard
WHERE title LIKE '%' || #{search} || '%'
WHERE content LIKE '%' || #{search} || '%'
WHERE memId=#{search}
ORDER BY ref DESC, step ASC) a WHERE rnum BETWEEN ${pageStart} AND ${pageEnd}

mapper.update:
UPDATE tipboard
SET hitcount = hitcount + 1
WHERE boardno = #{boardno}

UPDATE tipboard
SET content=#{content}, title=#{title}
WHERE boardno=#{boardno}

UPDATE tipboard
SET del = 1
WHERE boardno=#{boardno}

mapper.select:
SELECT category
FROM tipboard
WHERE boardno = #{parentno}
