mapper.select:
SELECT nvl(count(*), 0)
FROM tipcomment
WHERE bbsSeq=#{seq}

mapper.update:
UPDATE tipcomment
SET del = 1
WHERE seq = #{seq}

mapper.select:
SELECT seq, memId, content, bbsSeq, created, del
FROM tipcomment
WHERE seq=#{seq}

mapper.update:
UPDATE tipcomment
SET content = #{content}
WHERE seq = #{seq}
